import React, {Component} from 'react';
import CardList from './CardList';
import {cats} from './cats';
import SearchBox from './SearchBox';




class App extends Component {
	constructor() {
		super();
		this.state = {
			cats: cats,
			searchfield: ''
		}
	}

	onSearchChanges(event){
		console.log(event.target.value);
		const filtercats = this.state.cats.filter(cat => {
			return cat.name.toLowerCase().includes(thissearchfield.toLowerCase());
		})	
	}

	render(){
		return (
		<div className='tc'>
			<h1>Kitties</h1>
			<SearchBox searchChange={this.onSearchChanges} />
			<CardList cats={this.state.cats}/>;
		</div>
		);	
	}
}

export default App;
