import React, {Component} from 'react';
import CardList from './CardList';

import SearchBox from './SearchBox';
import './App.css';


class App extends Component { //smart component
	constructor() {
		super();
		this.state = {
			cats: [],
			searchfield: ''
		}
	}

	componentDidMount(){
		fetch('http://jsonplaceholder.typicode.com/users')
		.then(response => response.json())
		.then(users => this.setState({cats : users}));
	}

	onSearchChanges = (event) =>{
		this.setState({searchfield: event.target.value})
	}

	render(){
		const filtercats = this.state.cats.filter(cat => {
			return cat.name.toLowerCase().includes(this.state.searchfield.toLowerCase());
		})	
		return (
		<div className='tc'>
			<h1 className='f1'>Kitties</h1>
			<SearchBox searchChange={this.onSearchChanges} />
			<CardList cats={filtercats}/>;
		</div>
		);	
	}
}

export default App;
